import MyNavbar from "../../components/navbar/MyNavbar";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './AddArticle.css';
import axios from "axios";
import { useState } from "react";
import Swal from "sweetalert2";

function AddArticle(){
  const [formData, setFormData] = useState({})

  const addArticleHandler = () => {
    axios.post('http://localhost:8000/articles1', formData)
    .then(response => {
      if(response.status=== 201){
        Swal.fire({
          title: 'Article Created',
          text: 'Your article has been created successfully!',
          icon: 'success',
          confirmButtonText: 'Cool'
        })
      }
      })
    .catch(error => console.log(error))

  }

  const formHandler = (e) => {
    setFormData({...formData ,[e.target.name]: e.target.value})
   }



  return(
   <>
   <MyNavbar/>
   <div className="formContainer">
   <Form>
        <Form.Group className="mb-3">
        <Form.Label>Article Title</Form.Label>
        <Form.Control 
        name="title"
        onChange={formHandler} type="text" placeholder="Please Enter Article Title" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Short explanation</Form.Label>
        <Form.Control 
        name="desc"
        onChange={formHandler} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Writer</Form.Label>
        <Form.Control 
        name="writer"
        onChange={formHandler} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Article Topic</Form.Label>
        <Form.Control 
        name="category"
        onChange={formHandler} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Image Article </Form.Label>
        <Form.Control 
        name="image"
        onChange= {formHandler} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Time of reading</Form.Label>
        <Form.Control
        name="readingTime"
        onChange={formHandler} type="number" placeholder="" />
        </Form.Group>

      <Button onClick={addArticleHandler} variant="primary" type="button">
        Create Article
      </Button>
    </Form>
   </div>
   </>
  )
}
export default AddArticle;
