import MyNavbar from "../../components/navbar/MyNavbar";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './AddArticle.css';
import axios from "axios";
import { useState } from "react";

function AddArticle(){

  const [formData, setFormData] = useState({})

  const formHandler = (e, propertyName) => {
    setFormData({[propertyName]: e.target.value})


  }

  const addArticleHandler = () => {
    // axios.post('http://localhost:8000/articles', {
    //   "title":"The tiltle of the tenth article",
    //   "desc":"Lorem ipsum dolor sit amet consectetur adipiscing elit. Amet consectetur adipiscing elit quisque faucibus ex sapien. Quisque faucibus ex sapien vitae pellentesque sem placerat. Vitae pellentesque sem placerat in id cursus mi.",
    //   "image":"https://dl.next1code.ir/images/react/article8.webp",
    //   "readingTime":50,
    //   "category":"User Interface",
    //   "writer":"Nahid Karimi"
    // })
  }
  return(
   <>
   <MyNavbar/>
   <div className="formContainer">
   <Form>
        <Form.Group className="mb-3">
        <Form.Label>Article Title</Form.Label>
        <Form.Control onChange={(e) => formHandler(e, 'title')} type="text" placeholder="Please Enter Article Title" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Short explanation</Form.Label>
        <Form.Control onChange={(e) => formHandler(e, 'desc')} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Writer</Form.Label>
        <Form.Control onChange={(e) => formHandler(e, 'writer')} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Article Topic</Form.Label>
        <Form.Control onChange={(e) => formHandler(e, 'category')} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Image Article </Form.Label>
        <Form.Control onChange={(e) => formHandler(e, 'image')} type="text" placeholder="" />
        </Form.Group>

        <Form.Group className="mb-3">
        <Form.Label>Time of reading</Form.Label>
        <Form.Control onChange={(e) => formHandler(e, 'readingTime')} type="number" placeholder="" />
        </Form.Group>

      <Button onClick={addArticleHandler} variant="primary" type="button">
        Create Article
      </Button>
    </Form>
   </div>
   </>
  )
}
export default AddArticle;
